#filtrando dados e exibindo ordenando
df_filter = df_join.filter((df_join.id_cliente==1) &  (df_join.id_loja==2))

#filtrando dados e exibindo ordenando
df_filter.select("id_cliente","id_loja").orderBy(df_filter.id_cliente).show()

#agrupando dados com groupBy e AGG
